<?phpfunction __autoload($classname){	include_once("c/$classname.php");}include_once 'config.php';$action = 'action_';$action .= (isset($_GET['act'])) ? $_GET['act'] : 'index';$controller = new C_Page();if (isset($_GET['c'])) {    switch ($_GET['c'])    {	   case 'page':		  $controller = new C_Page();		  break;	   case 'regForm':	      $controller = new C_Reg();	       break;	   case 'personCab':	       $controller = new C_personCab();	       break;	   default:		  $controller = new C_Page();    }}$controller->Request($action);